mapper.select:
SELECT c.id, c.user_id, c.product_id, c.quantity, c.is_checked, p.name AS product_name, p.subtitle AS product_subtitle, p.main_image AS product_main_image, p.price AS product_price, p.status AS product_status, p.stock AS product_stock, (c.quantity * p.price) AS total_price FROM shopping_cart c INNER JOIN product p ON c.product_id = p.id WHERE c.user_id = #{customerId} AND c.status = 1 AND c.is_deleted = 0 AND p.is_deleted = 0
AND c.is_checked = #{isChecked}
ORDER BY c.create_time DESC

