mapper.select:
SELECT 
FROM order_item
WHERE user_id = #{userId}
AND is_deleted = 0

    AND order_no = #{orderNo}

ORDER BY create_time DESC

SELECT COUNT(*) as totalItems, SUM(quantity) as totalQuantity, SUM(total_price) as totalAmount, COUNT(DISTINCT order_no) as totalOrders, COUNT(DISTINCT product_id) as uniqueProducts FROM order_item WHERE is_deleted = 0
AND user_id = #{userId}
AND create_time >= #{startDate}
AND create_time <= #{endDate}
